mapper.select:
select note_id, note_type, cre_user_id, cre_user_name, profile
 ,display_id, reply_id, cre_date, reply_content 
 ,page_id, page_name 
from VIEW_NOTIFY
where re_user_id = #{logUser}
 and note_type = #{type}

mapper.update:
update tbl_member_notify
set read_check = 'TRUE'
where note_id = #{noteId}

update tbl_target_notify
set read_check = 'TRUE'
where note_id = #{noteId}
