mapper.select:
select display_id,title,description,img_adrs,url
from tbl_book_mark 
where display_id = #{displayId}

mapper.update:
update TBL_BOOK_MARK
set TITLE = #{title}
	,DESCRIPTION = #{description}
	,IMG_ADRS = #{imgAdrs}
	,url = #{url}
where display_Id = #{displayId}

update tbl_file
   set save_date  = sysdate
   	  ,up_name    = #{upName}
   	  ,new_name   = #{newName}
   	  ,path       = #{path}
 where display_id = #{displayId}
